import React from 'react';
import { NavLink } from 'react-router-dom';
import { useSelector } from 'react-redux';

const Navbar = () => {
    const state = useSelector(state => state.handleCart);

    const estiloNaranja = {
        backgroundColor: '#FFB366',
        color: '#fff', // Cambia el color del texto según sea necesario
    };

    const estiloAmarillo = {
        backgroundColor: '#FFD700',
        color: '#000', // Cambia el color del texto según sea necesario
    };

    const estiloRosa = {
        backgroundColor: '#E4007C',
        color: '#FFF', // Cambia el color del texto según sea necesario
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-light py-3 sticky-top" style={estiloRosa}>
            <div className="container">
                <NavLink className="navbar-brand fw-bold fs-4 px-2" to="/" style={{ color: '#fff' }}>COSITAS</NavLink>
                <button className="navbar-toggler mx-2" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>

                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className="navbar-nav m-auto my-2 text-center">
                        <li className="nav-item">
                            <NavLink className="nav-link" to="/" style={{ color: '#fff' }}>Inicio</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link" to="/product" style={estiloAmarillo}>Top</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link" to="/about" style={estiloRosa}>Nosotros</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link" to="/contact" style={estiloRosa}>Contacto</NavLink>
                        </li>
                    </ul>
                    <div className="buttons text-center">
                        <NavLink to="/login" className="btn btn-outline-dark m-2"><i className="fa fa-sign-in-alt mr-1"></i> Iniciar Sesión</NavLink>
                        <NavLink to="/register" className="btn btn-outline-dark m-2"><i className="fa fa-user-plus mr-1"></i> Registrarte</NavLink>
                        <NavLink to="/cart" className="btn btn-outline-dark m-2"><i className="fa fa-cart-shopping mr-1"></i> Cositas ({state.length})</NavLink>
                    </div>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
